import React, { useEffect, useState } from 'react';
import { PopupWithForm } from './PopupWithForm';

function AddPlacePopup(props) {
  const [image, setImage] = useState(null);
  const [description, setDescription] = useState(null);

  function handleSubmit(e) {
    e.preventDefault();

    props.onAddPlace({
      description,
      image
    });
  }

  return (
    <PopupWithForm title="Новое место" name="add" isOpen={props.isOpen} onClose={props.onClose}>
      <input
        id="input-name"
        name="input-name"
        type="text"
        className="popup__input popup__input_mesto"
        placeholder="Название"
        minLength="2"
        maxLength="30"
        required
      />
      <span className="popup__input-error input-name-error"></span>
      <input
        id="input-link"
        name="input-link"
        type="url"
        className="popup__input popup__input_image"
        placeholder="Ссылка на картинку"
        required
      />
      <span className="popup__input-error input-link-error"></span>
      <button type="submit" className="popup__button" aria-label="Сохранить">
        Сохранить
      </button>
    </PopupWithForm>
  );
}

export default AddPlacePopup;
