const PopupAddPlace = () => {
  return (
    <>
      <input
        id="input-name"
        name="input-name"
        type="text"
        className="popup__input popup__input_mesto"
        placeholder="Название"
        minLength="2"
        maxLength="30"
        required
      />
      <span className="popup__input-error input-name-error"></span>
      <input
        id="input-link"
        name="input-link"
        type="url"
        className="popup__input popup__input_image"
        placeholder="Ссылка на картинку"
        required
      />
      <span className="popup__input-error input-link-error"></span>
      <button type="submit" className="popup__button" aria-label="Сохранить">
        Сохранить
      </button>
    </>
  );
};

const PopupRemove = () => {
  return (
    <>
      <button type="submit" className="popup__button popup__button_remove" aria-label="Сохранить">
        Да
      </button>
    </>
  );
};

const PopupEditAvatar = () => {
  return (
    <>
      <input
        id="input-avatar"
        name="input-avatar"
        type="url"
        className="popup__input popup__input_avatar"
        placeholder="Ссылка на картинку"
        required
      />
      <span className="popup__input-error input-avatar-error"></span>
      <button type="submit" className="popup__button" aria-label="Сохранить">
        Сохранить
      </button>
    </>
  );
};

export { PopupWithForm, PopupAddPlace, PopupRemove, PopupEditAvatar };
