import headerLogo from '../images/logo.svg';
import { Route, Routes, Link, useLocation } from 'react-router-dom';

function Header(props) {
  const location = useLocation();

  return (
    <header className="header">
      <img className="header__logo" alt="Логотип" src={headerLogo} />
      {location.pathname === '/' ? <p className="header__email">{props.email}</p> : null}
      <Routes>
        <Route exact path="/"></Route>
        <Route exact path="/sign-in"></Route>
        <Route exact path="/sign-up" element={<Register />}></Route>
      </Routes>
      {/* <Link
        to="/sign-in"
        className="header__link header__link_exit"
        // onClick={}
      >
        Выйти
      </Link>
      <Link to="/sign-up" className="header__link">
        Войти
      </Link>
      <Link to="/sign-in" className="header__link">
        Регистрация
      </Link> */}
      {location.pathname === '/' ? (
        <Link to="/sign-in" className="header__link header__link_exit">
          Выйти
        </Link>
      ) : null}
      {location.pathname === '/sign-in' ? (
        <Link to="/sign-up" className="header__link">
          Регистрация
        </Link>
      ) : null}
      {location.pathname === '/sign-up' ? (
        <Link to="/sign-in" className="header__link">
          Войти
        </Link>
      ) : null}
    </header>
  );
}

export default Header;
