import React, { useEffect } from 'react';
import profileAvatar from '../images/avatar.svg';
import { api } from '../utils/Api';

function Main(props) {
  const [userName, setUserName] = React.useState(null);
  const [userDescription, setUserDescription] = React.useState(null);
  const [userAvatar, setUserAvatar] = React.useState(profileAvatar);
  const [cards, setCards] = React.useState('[]');

  useEffect(() => {
    api
      .getInfoUser()
      .then(userInfo => {
        setUserName(userInfo.name);
        setUserDescription(userInfo.about);
        setUserAvatar(userInfo.avatar);
      })
      .catch(error => {
        console.error(`Ошибка при получении данных: ${error}`);
      });
    api
      .getInitialCards()
      .then(cardsData => {
        setCards(cardsData.cards);
      })
      .catch(error => {
        console.error(`Ошибка при получении данных: ${error}`);
      });
  }, []);

  return (
    <>
      <main>
        <section className="profile">
          <div className="profile__container">
            <img
              className="profile__avatar"
              alt="Фото профиля"
              src={userAvatar}
              style={{ backgroundImage: `url(${userAvatar})` }}
            />
            <button
              type="button"
              className="profile__button-avatar"
              aria-label="Редактировать фото"
              onClick={props.onEditAvatar}
            ></button>
          </div>
          <div className="profile__info">
            <div className="profile__name">
              <h1 className="profile__title">{userName}</h1>
              <button
                type="button"
                className="profile__edit-button"
                aria-label="Редактировать"
                onClick={props.onEditProfile}
              ></button>
            </div>
            <p className="profile__subtitle">{userDescription}</p>
          </div>
          <button
            type="submit"
            className="profile__add-button"
            aria-label="Добавить контент"
            onClick={props.onAddPlace}
          ></button>
        </section>
        <section className="elements">
          <ul className="elements__list">
            {cards.map(card => {
              <template id="template-element">
                <div className="element">
                  <button
                    type="button"
                    className="element__button-remove"
                    aria-label="Удалить"
                  ></button>
                  <img
                    src={card.link}
                    className="element__image"
                    alt={card.name}
                    style={{ backgroundImage: `url(${card.link})` }}
                  />
                  <div className="element__description">
                    <h2 className="element__title">{card.name}</h2>
                    <div className="element__likes">
                      <button
                        type="button"
                        className="element__button-like"
                        aria-label="Нравиться"
                      ></button>
                      <span className="element__like-number"></span>
                    </div>
                  </div>
                </div>
              </template>;
            })}
          </ul>
        </section>
      </main>
    </>
  );
}

export default Main;
