import React, { useEffect, useState } from 'react';
// import profileAvatar from '../images/avatar.svg';
import { api } from '../utils/Api';
import Card from './Card';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Main(props) {
  // const [cards, setCards] = useState([]);
  const cards = props.cards;
  const currentUser = React.useContext(CurrentUserContext);
  // console.log(props.setCards);

  useEffect(() => {
    api
      .getInitialCards()
      .then(cardsData => {
        // console.log('cardsData:', cardsData);
        props.setCards(cardsData);
      })
      .catch(error => {
        console.error(`Ошибка при получении данных: ${error}`);
      });
  }, []);

  console.log(props.onCardLike);
  // console.log(props.onCardClick);

  return (
    <main>
      <section className="profile">
        <div className="profile__container">
          <img
            className="profile__avatar"
            alt="Фото профиля"
            src={currentUser ? currentUser.avatar : ''}
            style={{ backgroundImage: `url(${currentUser ? currentUser.avatar : ''})` }}
          />
          <button
            type="button"
            className="profile__button-avatar"
            aria-label="Редактировать фото"
            onClick={props.onEditAvatar}
          ></button>
        </div>
        <div className="profile__info">
          <div className="profile__name">
            <h1 className="profile__title">{currentUser ? currentUser.name : ''}</h1>
            <button
              type="button"
              className="profile__edit-button"
              aria-label="Редактировать"
              onClick={props.onEditProfile}
            ></button>
          </div>
          <p className="profile__subtitle">{currentUser ? currentUser.about : ''}</p>
        </div>
        <button
          type="submit"
          className="profile__add-button"
          aria-label="Добавить контент"
          onClick={props.onAddPlace}
        ></button>
      </section>
      <section className="elements">
        <ul className="elements__list">
          {cards.map((card, i) => (
            <li key={card._id}>
              <Card card={card} onCardClick={props.onCardClick} onCardLike={props.onCardLike} />
            </li>
          ))}
        </ul>
      </section>
    </main>
  );
}

export default Main;
