import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import {
  PopupWithForm,
  PopupEditProfile,
  PopupAddPlace,
  PopupRemove,
  PopupEditAvatar
} from './PopupWithForm';
import ImagePopup from './ImagePopup';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);

  function handleEditProfileClick() {
    // document.querySelector('.popup_edit-profile').classList.add('popup_opened');
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    // document.querySelector('.popup_add-card').classList.add('popup_opened');
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    // document.querySelector('.popup_update-avatar').classList.add('popup_opened');
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick() {
    setSelectedCard(true);
  }

  function closeAllPopups() {
    // console.log('Попапы закрыты');
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(false);
  }

  return (
    <div className="page__content">
      <Header />
      <Main
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}
      />
      <Footer />
      <PopupWithForm
        title="Редактировать профиль"
        name="edit"
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
      >
        <PopupEditProfile />
      </PopupWithForm>
      <PopupWithForm
        title="Новое место"
        name="add"
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      >
        <PopupAddPlace />
      </PopupWithForm>
      <PopupWithForm title="Вы уверены?" name="remove" onClose={closeAllPopups}>
        <PopupRemove />
      </PopupWithForm>
      <PopupWithForm
        title="Обновить аватар"
        name="update"
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      >
        <PopupEditAvatar />
      </PopupWithForm>
      <ImagePopup onClose={closeAllPopups} />
    </div>
  );
}

export default App;
